import Map from "./components/Map"

class App extends Component {
	state ={
		place :""
	}
  area = (place)=>{
  	this.setState({place:place})
  }
  render(){
  	console.log(this.state,"app")
  		return (
   			<div>
			    <AppNavBar/>
			    <Container>

			      	<Map
			      	style={{width:"300px"}}
     				google={this.props.google}
				    center={{lat: -33.78, lng: 151.2}}
				    height='500px'
				    zoom={8}
					/>
			    </Container>

			</div>



  );
  }

}

export default App;
