import React, { Component } from 'react';
import { Marker  } from "react-google-maps";
import basket from "../image/basket.png"
import basketSelected from "../image/basketSelected.png"


export class Markers extends Component {
	componentDidMount(){
		this.updating_marker()
	}
	updating_marker = ()=>{
		console.log("updating marker>")
		if (this.state.moving===true){
			var markers1 = this.insideBound_bound(this.state.markers,this.state.bound)
			this.setState({
				markers1:markers1,
				moving:false,
				input:false,
				maploading:false
			})
		}else{
			markers1 = this.insideBound(this.state.markers,this.state.movePosition)
			this.setState({
				markers1:markers1,
				moving:false,
				input:false,
				maploading:false,
			})		
		}
		}
	shouldComponentUpdate( nextProps, nextState ){	
		console.log("should Componeent update", nextState, this.state)
		if (this.check_markers1(nextState.markers1,this.state.markers1)===false){
			console.log("change")
			return true
		}else if (this.check_markers1(nextState.markers1,this.state.markers1)===true){
			console.log("no change")
			return false
		}}
	componentDidUpdate(prevProps, prevState, snapshot){
		console.log("componentDidUpdate",prevState)
		this.updating_marker()
	}
	check_markers1=(array1,array2) => {
		var arr1 = []
		var arr2 = []
		array1.map((elem)=>{
			arr1.push(elem.photo_id)
		})
		array2.map((elem)=>{
			arr2.push(elem.photo_id)
		})
		if (arr1.sort().toString()==arr2.sort().toString()){	
			return true
		}else 
			{return false}
		}
	insideBound = (shops,center)=>{
		if (center!=null&&shops!=null){
			var result = []
			shops.map((shop)=>{
				if (shop.lat>center.lat-0.2 && shop.lat<center.lat+0.2 && shop.lng>center.lng-0.2 && shop.lng<center.lng+0.2){
					result.push(shop)}	
			})
			console.log(result,"lets look center",center,shops)	
			this.props.markersInBound(result)		
			return result			
		}else{return []}		
	}
	insideBound_bound = (shops,bound)=>{
		if (bound!=null&&shops!=null){
			var result = []	
			shops.map((shop)=>{
				if (shop.lat>bound.na.j && shop.lat<bound.na.l && shop.lng>bound.ia.j && shop.lng<bound.ia.l){
					result.push(shop)}	
			})
			console.log(result,"lets look bound",bound,shops)		
			this.props.markersInBound(result)		
			return result			
		}else{return []}
	}
	onDblClick = (marker,event)=>{	
		var mark = this.state.markers1.filter((mark)=>{
			return mark.photo_id=== marker
		})
		mark = mark[0]
		this.props.markerSelected(mark)
		this.setState({markerSelected:mark.photo_id, icon:true})
	} 
	iconBasket = (marker)=>{
		let icon
		if (this.props.selected.photo_id===marker.photo_id){
				icon = basketSelected
			}else{
				icon = basket
			}
		return icon	
	}	 
render()
	{this.state.markers1.map((marker) => {				
		console.log(this.state.markerSelected,"markerselected",marker.photo_id,"are they the same")
	return( 
		<Marker
				key={marker.photo_id}
				position={{ lat: marker.lat, lng: marker.lng }}
				icon ={this.iconBasket(marker)}
				onDblClick = {this.onDblClick.bind(this,marker.photo_id)}
				animation={window.google.maps.Animation.DROP}
		/>
	)
		
	})}		

}
export default Markers
