import React, {Component} from "react"
import { withGoogleMap, GoogleMap, withScriptjs, Marker } from "react-google-maps";
import Autocomplete from 'react-google-autocomplete';
import Geocode from "react-geocode";
Geocode.setApiKey("AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI");
Geocode.enableDebug();


class Shop extends React.Component{
    constructor( props ){
        super( props );
        this.state = {
            completeAddress :"",
            address: '',
            city: '',
            postcode: '',
            state: '',
        }
    }

    shouldComponentUpdate( nextProps, nextState ){
        console.log("shop shouldComponentUpdate",nextProps,this.props,nextState,this.state)
        if (
            this.props.mapPosition.lat !== nextProps.mapPosition.lat ||
            this.state.address !== nextState.address ||
            this.state.city !== nextState.city ||
            this.state.area !== nextState.area ||
            this.state.state !== nextState.state
        ){return true
        } else { return false
        }
    }

    getStreetNumber = ( addressArray ) => {
        let StreetNumber = '';
        for( let i = 0; i < addressArray.length; i++ ) {
            if ( addressArray[ i ].types[0] && 'street_number' === addressArray[ i ].types[0] ) {
                StreetNumber = addressArray[ i ].long_name;
                return StreetNumber;
            }
        }
    }

    getStreet = ( addressArray ) => {
        let Street = '';
        for( let i = 0; i < addressArray.length; i++ ) {
            if ( addressArray[ i ].types[0] && 'route' === addressArray[ i ].types[0] ) {
                Street = addressArray[ i ].long_name;
                return Street;
            }
        }
    }

    getPostcode = ( addressArray ) => {
        let postcode = '';
        for( let i = 0; i < addressArray.length; i++ ) {
            if ( addressArray[ i ].types[0] && 'postal_code' === addressArray[ i ].types[0] ) {
                postcode = addressArray[ i ].long_name;
                return postcode;
            }
        }
    };

    getCity = ( addressArray ) => {
        let city = '';
        for( let i = 0; i < addressArray.length; i++ ) {
            if ( addressArray[ i ].types[0]  ) {
            for ( let j = 0; j < addressArray[ i ].types.length; j++ ) {
                if ( 'sublocality_level_1' === addressArray[ i ].types[j] || 'locality' === addressArray[ i ].types[j] ) {
                    city = addressArray[ i ].long_name;
                    return city;
                }
            }
        }
        }
    };

    getState = ( addressArray ) => {
    let state = '';
        for( let i = 0; i < addressArray.length; i++ ) {
            for( let i = 0; i < addressArray.length; i++ ) {
                if ( addressArray[ i ].types[0] && 'administrative_area_level_1' === addressArray[ i ].types[0] ) {
                    state = addressArray[ i ].short_name;
                    return state;
                }
            }
        }
    };
    onPlaceSelected = ( place,event ) => {
        console.log(place)
        if (place.name!==undefined){
            Geocode.fromAddress(place.name).then(
                response => {
                const { lat, lng } = response.results[0].geometry.location
                console.log(lat, lng)
                if (lat ===undefined){
                    return null
                }
                this.props.mapCoord({
                        lat: lat,
                        lng: lng
                    })
                this.props.markCoord({
                        lat: lat,
                        lng: lng
                    })
            })
        }
        else{
            const name = place.formatted_name,
            latValue = place.geometry.location.lat(),
            lngValue = place.geometry.location.lng();
            this.props.mapCoord({
                    lat: latValue,
                    lng: lngValue
                })
            this.props.markCoord({
                    lat: latValue,
                    lng: lngValue
                })
        }
     
    }
    onMarkerDragEnd = ( event ) => {
        console.log( 'event', event );
        let newLat = event.latLng.lat(),
        newLng = event.latLng.lng(),
        addressArray = [];
        Geocode.fromLatLng( newLat , newLng ).then(
        response => {
            const completeAddress = response.results[0].formatted_address,
            addressArray =  response.results[0].address_components,
            street_number =this.getStreetNumber(addressArray),
            street =this.getStreet(addressArray),
            city = this.getCity( addressArray ),
            postcode = this.getPostcode( addressArray ),
            state = this.getState( addressArray ),
            address = street_number +" "+street
            this.props.markerInfo({
                completeAddress: ( completeAddress ) ? completeAddress : '',
                address: ( address ) ? address : '',
                postcode: ( postcode ) ? postcode : '',
                city: ( city ) ? city : '',
                state: ( state ) ? state : '',
                markerPosition: {
                lat: newLat,
                lng: newLng
                },
                mapPosition: {
                lat: newLat,
                lng: newLng
                },
                } )
            this.setState({
                markerPosition: {
                lat: newLat,
                lng: newLng
                },
            })
        },

        error => {
            console.error(error);
        }
    );
    };
    render(){
        console.log("shop",this.state,this.props)
        const AsyncMap = withScriptjs(
            withGoogleMap(
                props => (
                    <GoogleMap google={this.props.google}
                        defaultZoom={this.props.zoom}
                        center={{ lat: this.props.mapPosition.lat, lng: this.props.mapPosition.lng }}
                    >
                        <Marker 
                            google={this.props.google}
                            draggable ={true}
                            onDragEnd = {this.onMarkerDragEnd}
                            position={{ lat: this.props.markerPosition.lat, lng: this.props.markerPosition.lng }}
                        />
                        <Marker />
                        <Autocomplete
                            style={{
                            width: '100%',
                            height: '40px',
                            paddingLeft: '16px',
                            margin:"2px auto 0px auto",
                            }}
                            onPlaceSelected={ this.onPlaceSelected }
                            types={['(regions)']}
                        />
                    </GoogleMap>
                )
            )
        );
        let map;
        if( this.props.mapPosition.lat !== undefined ) {
        map = 
            <React.Fragment>
                <AsyncMap
                    googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI&libraries=places"
                    loadingElement={
                        <div style={{ height: `100%` }} />}
                    containerElement={
                        <div style={{   height: this.props.height,
                            display: "flex",
                            flexDirection: "column-reverse",
                            marginTop:"20px"}} /> }
                    mapElement={
                        <div style={{ height: `100%` }} />}
                />
            </React.Fragment>
        } else {
        map = <div style={{height: this.props.height}} />
        }
        
    return( map )
    }
}
    

export default Shop
