import React, {Component} from "react"
import {CardText,Table,CardTitle,CardSubtitle,Card,Container, Row,Col,CardImg,CardBody} from 'reactstrap';


export class CardShops extends Component{
	constructor(props){
		super(props)
		this.state={
			space1:"",
			space2:"",
			space3:"",
			basket:[],
			itemOnDrag:"",
			
		}
	}
	shouldComponenntUpdate(nextProps,nextState){
		if(this.props.shopSelectedCompare!==nextProps.shopSelectedCompare){
			return true
		}if(this.props.basket!==nextProps.basket){
			return true
		}
	}
	componentDidUpdate(prevProps,prevState){
		const {space1,space2,space3} = this.state
		if(this.props.shopSelectedCompare!==prevProps.shopSelectedCompare){
			this.shopSelectedCompare(this.props.shopSelectedCompare)
			this.props.allSpace(false)
		}if (this.props.basket!==prevProps.basket){
			this.setState({basket:this.props.basket})
		}
	}
	shopSelectedCompare=(markerObject)=>{
		const {space1,space2,space3} = this.state
		if (space1===""&&space2===""&&space3===""){
			this.setState({space1:markerObject})
		}else if(space2===""&&space3===""){
			this.setState({space2:markerObject})
		}else if(space3===""){
			this.setState({space3:markerObject})
		}
	}
	render(){
		console.log("cardShop",this.state,this.props)
		var cardComparsion=(space) => ( 
			<Card body className="text-center">
				<h1>{space.name}</h1>
				<CardSubtitle>{space.completeAddress}</CardSubtitle>
				<Table>
			        <thead>
			          <tr>
			            <th>Item Name</th>
			            <th>Price</th>
			          </tr>
			        </thead>
			        <tbody>
			        	{this.state.basket.map((item)=>{
			        		return(
			        			<tr>
			        				<td>{item.result.title}</td>
			        				<td>{item.price}</td>
			        			</tr>
			        			)
			        	})}
			        </tbody>
			    </Table>
			</Card>
			)
		const cardNoShop =
			<Card body className="text-center">
				<h1>Add a shop to Compare</h1>
			</Card>
		return(
			<React.Fragment>
				<Col sm={3}>
					{this.state.space1!==""?cardComparsion(this.state.space1):cardNoShop}
				</Col>
				<Col sm={3}>
					{this.state.space2!==""?cardComparsion(this.state.space2):cardNoShop}
				</Col>
				<Col sm={3}>
					{this.state.space3!==""?cardComparsion(this.state.space3):cardNoShop}
				</Col>
			</React.Fragment>	
			)
	}
}

export default CardShops
