import React, {Component} from "react"
import SearchShop from "./SearchShop"
import FormItem from "./AddItem/FormItem"
import {CardTitle,Card,Container, Row,Col,CardImg,CardBody} from 'reactstrap';
import CBasket from "../image/CBasket.png"


export class CompareBasket extends Component{
	constructor(props){
		super(props)
		this.state={
			supermarket_selected:"",
			
		}
	}
	
	supermarket_selected=(markerObject)=>{
		this.setState({supermarket_selected:markerObject})
	}
	ondragover=(event)=>{
		console.log("dragover")
		event.preventDefault()
	}
	ondragenter=(event)=>{
		console.log("drag enter",event.target)
		if(event.target.className==="cardBasket"){
			document.getElementById("cardBasket").style.opacity = "0.4"	
			document.getElementById("cardBasket").style.backgroundColor = "green"	
		}
	}
	ondrop=(event)=>{
		event.preventDefault();
		console.log("onDrop")
	}
	ondragleave=(event)=>{
		if(event.target.id==="cardBasket"){
			event.target.style.opacity="1"
			event.target.style.backgroundColor="inherit"
		}
		console.log("onDragLeave")
	}
	shopSelectedCompare=(markerObject,event)=>{
		console.log(markerObject,"compareBasketshop")
	}
	render(){
		let shopSelected = 
				<FormItem
					supermarket_selected={this.state.supermarket_selected}
				/>
	

		if(this.state.supermarket_selected===""){shopSelected=null}
		return (
			<React.Fragment>
				<SearchShop
					supermarket_selected={this.supermarket_selected}
					fromAddItem = {true}
					compareBasket={true}
					shopSelectedCompare={this.shopSelectedCompare}
					/>
				<Container style={{marginTop:"50px"}}>
					<Row>
						<Col sm={3}  style={{zIndex:"1"}}>
							<Card body className="text-center cardBasket" id="cardBasket" 
								onDragOver = {this.ondragover}
								onDrop = {this.ondrop} 
							    onDragEnter = {this.ondragenter}
							    onDragLeave ={this.ondragleave}
							    style={{zIndex:"-1"}}>
								<CardTitle className="cardBasket" >Drag the Item to Add to the basket</CardTitle>
								<CardBody className="cardBasket">
									<img width="50%" src={CBasket} alt="Card image cap" style={{margin:"auto"}} className="cardBasket" />
								</CardBody>
							</Card>
							{shopSelected}
						</Col>		
						<Col sm={3}>
							<Card body className="text-center">
							<h1>Drag a shop to Compare</h1>
							</Card>
						</Col>
						<Col sm={3}>
							<Card body className="text-center">
								<h1>Drag a shop to Compare</h1>
							</Card>
						</Col>
						<Col sm={3}>
							<Card body className="text-center">
								<h1>Drag a shop to Compare</h1>
							</Card>
						</Col>
					</Row>
				</Container>
			</React.Fragment>
			)
	}
}

export default CompareBasket
