import React, { Component } from 'react';
import basket from "../image/basket.png"
import basketSelected from "../image/basketSelected.png"
import { Marker,InfoWindow  } from "react-google-maps";

export class MarkerShop extends Component {
	constructor(props){
		super(props)
		this.state={
			markerSelected:"",
		}

	}
	shouldComponentUpdate(nextProps,nextState){
		console.log("should Componeent update", nextProps, this.props)
		if (nextProps.selected !== this.props.selected){
			return true
		}else{
			return false
		}
	}
	componentDidUpdate(prevProps, prevState, snapshot){
		console.log("componentDidUpdate",prevState,this.state,prevProps,this.props,snapshot)
		if(this.props.selected!==prevProps.selected){
			this.setState({markerSelected:this.props.selected})
		}
	}
	onDblClick = (marker,event)=>{	
		var mark = this.props.markers1.filter((mark)=>{
			return mark.photo_id=== marker
		})
		mark = mark[0]
		this.props.markerSelected(mark)
		this.setState({markerSelected:mark.photo_id})
	} 
	iconBasket = (marker)=>{
		let icon
		if (this.state.markerSelected===marker.photo_id){
				icon = basketSelected
			}else{
				icon = basket
			}
		return icon	
	}	  		
	render(){
		console.log(this.state)
		return(
			this.props.markers1.map((marker) => {				
				console.log(this.state.markerSelected,"markerselected",marker.photo_id,"are they the same")
			return( 
				<Marker
						key={marker.photo_id}
						position={{ lat: marker.lat, lng: marker.lng }}
						icon ={this.iconBasket(marker)}
						onDblClick = {this.onDblClick.bind(this,marker.photo_id)}
						animation={window.google.maps.Animation.DROP}
				>

				</Marker>
			)
				
		})

    	)

	}

	}


export default MarkerShop
