import React, {Component} from "react"
import { Col, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class InfoShop extends Component{
	constructor( props ){
		super( props )
		this.state = {
			name:"",
			address: '',
			city: '',
			state: '',
		}
	}
	onChange = ( event ) => {
  		this.setState({ [event.target.name]: event.target.value });
 	};
	render(){
		return(
		<Form>
			<FormGroup>
				<Label htmlFor="">Name</Label>
				<Input type="text" name="name" className="form-control" onChange={ this.onChange } value={ this.state.name }/>
			</FormGroup>
			<FormGroup>
				<Label htmlFor="">Address</Label>
				<Input type="text" name="address" className="form-control" onChange={ this.onChange } value={ this.state.address }/>
			</FormGroup>
			<FormGroup>
				<Label htmlFor="">City</Label>
				<Input type="text" name="city" className="form-control" onChange={ this.onChange }  value={ this.state.city }/>
			</FormGroup>
			<FormGroup>
				<Label htmlFor="">State</Label>
				<Input type="text" name="state" className="form-control" onChange={ this.onChange } value={ this.state.state }/>
			</FormGroup>

		</Form>
		)
	}
}

export default InfoShop
