import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import AppNavBar from "./components/AppNavBar"
import { Container, Row,Col } from 'reactstrap';
import InfoArea from "./components/InfoArea"
import AddShop from "./components/AddShop"
import Map from "./components/Map"

class App extends Component {
	state ={
		markerSelected :"",
		markersInBound:[],
		markers:[{photo_id:0,lat: -33.78, lng: 151.2,name:"add1",address:"asdfddfd",score:"50.32",details:"details"},
				{photo_id:10,lat: -33.79, lng: 151.1,name:"add22",address:"asdfd",score:"54.32",details:"details"},
				{photo_id:2,lat: -33.785, lng: 151.23,name:"add3",address:"asdfd",score:"20.32",details:"details"},
				{photo_id:5,lat: -31.95, lng: 115.8605,name:"add4",address:"asdfd",score:"30.32",details:"details"},
				{photo_id:1,lat: -34.070587, lng: 150.791252,name:"add2",address:"asdfd",score:"35.32",details:"details"}],
		markers1:[],
	}
  markerSelected=(marker)=>{
  	this.setState({markerSelected:marker})  }
  markersInBound = (markers)=>{
  	this.setState({markersInBound:markers})
  }

  render(){

  	console.log(this.state,"app")
  		return (
   			<div>
			    <AppNavBar/>
			    <Container>
			    	<div className="title">
						<h1>
							Search for the Supermarkets near you
						</h1>

					</div>
			    	<Row>
			    		<Col sm={6}>
					      	<Map
		     				google={this.props.google}
						    center={{lat: -33.78, lng: 151.2}}
						    height='500px'
						    zoom={8}
						    markerSelected = {this.markerSelected}
						    markersInBound = {this.markersInBound}
						    selected = {this.state.markerSelected}
							/>
						</Col>
						<Col sm={6}>
							<InfoArea
								markerSelected	={this.state.markerSelected}
								markersInBound	={this.state.markersInBound}
								selected = {this.markerSelected}
							/>
						</Col>
					</Row>
			    </Container>


			</div>



  );
  }

}

export default App;
