import React, {Component} from "react"
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input
} from 'reactstrap';
import ReactSearchBox from 'react-search-box'


class SearchArea extends Component{
	state = {
		input:"",
		location:[{key:"Penrith",value:["Penrith, " ,"2750"]},{key:"Sydney",value:["Sydney, ","2000"]},{key:"Blacktown",value:["Blacktown, ","2148"]}]
	}
	onSelect = (event)=>{
		console.log(event)
		var input = ""
		typeof value === 'string'?input=event.key:input = event.value
		this.setState({
			input:input
		})
	}

	handleSubmit =(event)=>{
		event.preventDefault()
		console.log(event.target.value)
		this.props.area(event.target.value)
		this.setState({input:""})
	}
	
	render(){
		console.log(this.state)
		return (
			<div>
				<h1>
					Search for the Supermarkets near you
				</h1>
				<Form onSubmit={this.handleSubmit}>
				 	<ReactSearchBox
				        placeholder="Type in Postcode"
				        value={this.state.input} 
				        data={this.state.location}
				        callback={record => console.log(record)}
				        onSelect ={this.onSelect}
      				/>
					<Button color='dark' style={{ marginTop: '2rem' }} block>
                  		Search 
                	</Button>
				</Form>
			</div>


			)
	}



}
export default SearchArea
