import React, {Component} from "react"
import { ListGroup, ListGroupItem } from 'reactstrap';

import Geocode from "react-geocode";

export class InfoArea extends Component {
	markersInBound = (markers)=>{
		return markers.map((marker)=>{
			return(
				<ListGroupItem>
					<ListGroupItem>
						{marker.name}
					</ListGroupItem>
					<ListGroupItem>
						{marker.address}
					</ListGroupItem>
					<ListGroupItem>
						{marker.score}
					</ListGroupItem>
				</ListGroupItem>
				)
		})
	}
	focusOnMarker = (marker)=>{
		return(
			<ListGroupItem >
				<ListGroupItem active>
					{marker.name}
				</ListGroupItem>
				<ListGroupItem>
					{marker.address}
				</ListGroupItem>
				<ListGroupItem>
					{marker.score}
				</ListGroupItem>
				<ListGroupItem>
					{marker.details}
				</ListGroupItem>
			</ListGroupItem>

			)
	}
	render() {
	console.log(this.props)
	let info 
	if (this.props.markerSelected===""){
		console.log(this.props.markerSelected)
		info = 
				<div style={{border:"1px solid black", height:"438px", overflowY: "scroll"}}>
					<div >
						<h2> Stores in the area </h2>
					</div>
					<ListGroup>
						{this.markersInBound(this.props.markersInBound)}
					</ListGroup>
				</div>

	}else{
		info = 	
				<div style={{border:"1px solid black", height:"438px", overflowY: "scroll"}}>
					{this.focusOnMarker(this.props.markerSelected)}
				</div>
	

		}
	
	return (
		<React.Fragment>
		<div style={{height: '62px'}}>
		</div>
			{info}
		</React.Fragment>	
)
  }
}

export default InfoArea
