import React, {Component} from "react"
import Shop from "./AddShop/Shop"
import { Container, Row,Col } from 'reactstrap';
import InfoShop from "./AddShop/InfoShop"
class AddShop extends Component {
    constructor(props){
        super(props)
        this.state={
            markerPosition:{
                lat:-33.865143,
                lng: 151.209900
                    },
            markerInfo:""
        }
    }
    markerPosition=(center)=>{
        console.log(center)
        this.setState({markerPosition:center})

    }
    markerInfo  = (info)=>{
        this.setState({markerInfo:info})
    }
    render() {
        console.log("addShop",this.state)
        return(
            <div>
                <Container>
                    <Row>
                        <Col sm={6}>
                             <Shop
                                 google={this.props.google}
                                 center={{lat: this.state.markerPosition.lat, lng: this.state.markerPosition.lng}}
                                 height='500px'
                                 zoom={13}
                                 markerInfo={this.markerInfo}
                                 style ={{marginTop:"20px"}}
                            />
                        </Col>
                        <Col sm={6}>
                            <InfoShop
                                markerPosition={this.markerPosition}
                                markerInfo = {this.state.markerInfo}
                                />        
                        </Col>
                    </Row>
                </Container>
            </div>

        )}
    }
  export default AddShop
