import React, {Component} from "react"
import { ListGroup, ListGroupItem,ListGroupItemText,ListGroupItemHeading} from 'reactstrap';

import Geocode from "react-geocode";

export class InfoArea extends Component {
	constructor(props){
		super(props)
		this.state={
			markerHighlighted:"",
			markerSelected:""
		}
	}
	onclickMarker = (marker,event) =>{
		console.log(marker)
		this.setState({markerHighlighted:marker.photo_id,
						active:"success"})
	}
	onDoubleClick = (marker)=>{
		this.setState({markerSelected:marker.photo_id})

	}
	markersInBound = (markers)=>{
		return markers.map((marker)=>{
			let active = ""
			if (marker.photo_id===this.state.markerHighlighted){
				active = "success"
			}
			return(
				<ListGroupItem 
				onClick={this.onclickMarker.bind(this,marker)}  
				key={marker.photo_id}  
				color={active}
				onDoubleClick = {this.onDoubleClick.bind(this,marker)}>
					<ListGroupItemHeading>
						{marker.name}
					</ListGroupItemHeading>
					<ListGroupItemText>
						{marker.address}
					</ListGroupItemText>
					<ListGroupItemText>
						{marker.score}
					</ListGroupItemText>
				</ListGroupItem>
				)
		})
	}
	focusOnMarker = (marker)=>{
		return(
			<ListGroupItem >
				<ListGroupItem>
					{marker.name}
				</ListGroupItem>
				<ListGroupItem>
					{marker.address}
				</ListGroupItem>
				<ListGroupItem>
					{marker.score}
				</ListGroupItem>
				<ListGroupItem>
					{marker.details}
				</ListGroupItem>
			</ListGroupItem>

			)
	}
	render() {
	console.log(this.props)
	let info 
	if (this.props.markerSelected===""||this.state.markerSelected===""){
		console.log(this.props.markerSelected)
		info = 
				<div style={{border:"1px solid black", height:"438px", overflowY: "scroll"}}>
					<div >
						<h2> Stores in the area </h2>
					</div>
					<ListGroup>
						{this.markersInBound(this.props.markersInBound)}
					</ListGroup>
				</div>

	}else{
		info = 	
				<div style={{border:"1px solid black", height:"438px", overflowY: "scroll"}}>
					{this.focusOnMarker(this.props.markerSelected)}
				</div>
	

		}
	
	return (
		<React.Fragment>
		<div style={{height: '62px'}}>
		</div>
			{info}
		</React.Fragment>	
)
  }
}

export default InfoArea
