import React, {Component} from "react"
import Autocomplete from 'react-google-autocomplete';
import Geocode from "react-geocode";
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker  } from "react-google-maps";



Geocode.setApiKey('AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI');
Geocode.enableDebug();

export class SearchArea extends Component {
	state = {
		mapPosition:{
			lat: null,
			lng: null
		},
		markerPosition:{
			lat:null,
			lng:null
		},
		map:null,
		markers:[{photo_id:0,lat: -33.78, lng: 151.2,name:"add1"},{photo_id:10,lat: -33.79, lng: 151.1,name:"add22"},{photo_id:2,lat: -33.785, lng: 151.23,name:"add3"}]
	}
	onPlaceSelected = ( place,event ) => {
		console.log(place)
		if (place.name!==undefined){
			Geocode.fromname(place.name).then(
				response => {
    			const { lat, lng } = response.results[0].geometry.location
    			console.log(lat, lng)
    			if (lat ===undefined){
    				return null
    			}
    			this.props.SearchArea([lat,lng])
    		})
			return
		}
		else{
			const name = place.formatted_name,
			lat = place.geometry.location.lat(),
			lng = place.geometry.location.lng();
			this.props.SearchArea([lat,lng])
		}

	};

	render() {
		console.log(this.props)
	const AsyncMap = withScriptjs(
		withGoogleMap(
			props => (
				<GoogleMap 
				google={this.props.google}
				ref = {this.maploaded}
				>
					<Autocomplete
						style={{
						width: '200%',
						height: '40px',
						paddingLeft: '16px',
						marginTop: '2px',
						marginBottom: '100px'
						}}
						onPlaceSelected={ this.onPlaceSelected }
						types={['(regions)']}
						placeholder={"Enter and select location"}
					/>
				
				</GoogleMap>
			)
		)
	)

	

	return (

			 <div style={{width:"500px"}}>

				<AsyncMap
					googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI&libraries=places"
					loadingElement={
					 <div style={{ height: `100%` }} />
					}
					containerElement={
					 <div style={{ height: this.props.height }} />
					}
					mapElement={
					 <div style={{ height: `100%` }} />
					}
				/>
			</div>


	 
	)
  }
}

export default SearchArea
