import React, {Component} from "react"
import Shop from "./AddShop/Shop"
import { Container, Row,Col } from 'reactstrap';
import InfoShop from "./AddShop/InfoShop"
class AddShop extends Component {
    constructor(props){
        super(props)
        this.state={
            markerPosition:{
                lat:-33.865143,
                lng: 151.209900
                    },
            markerInfo:"",
            mapPosition:{
                lat:-33.865143,
                lng: 151.209900
            }
        }
    }
    markerPosition=(center)=>{
        console.log(center)
        this.setState({markerPosition:center})

    }
    markerInfo  = (info)=>{
        this.setState({markerInfo:info})
    }
    mapPosition  = (center)=>{
        this.setState({mapPosition:center})
    }
    render() {
        console.log("addShop",this.state)
        return(
            <div>
                <Container>
                    <Row>
                        <Col sm={6}>
                             <Shop
                                 google={this.props.google}
                                 mapPosition={{lat: this.state.mapPosition.lat, lng: this.state.mapPosition.lng}}
                                 height='500px'
                                 zoom={13}
                                 markerInfo={this.markerInfo}

                            />
                        </Col>
                        <Col sm={6}>
                            <InfoShop
                                markerPosition={this.markerPosition}
                                markerInfo = {this.state.markerInfo}
                                mapPosition ={this.mapPosition}
                                />        
                        </Col>
                    </Row>
                </Container>
            </div>

        )}
    }
  export default AddShop
