import React, {Component} from "react"
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input
} from 'reactstrap';
import ReactSearchBox from 'react-search-box'


class SearchArea extends Component{
	state = {
		input:"",
		location:[{key:"Penrith",value:2750},{key:"Sydney",value:2000},{key:"Blacktown",value:2148}]
	}
	handleChange = (event)=>{
		this.setState({
			input:event.target.value
		})

	}
	completeSwap=(jsonPoint)=>{
		var newArray = []
		jsonPoint.map((point)=>{
			var newJson = {}
			newJson["value"]=point["key"]
			newJson["key"]=point["value"]
			newArray.push(newJson)

		})
		return [...newArray,...jsonPoint]
	}
	handleSubmit =(event)=>{
		event.preventDefault()
		this.props.area(this.state.input)
		this.setState({input:""})
	}
	
	render(){
		return (
			<div>
				<h1>
					Search for the Supermarkets near you
				</h1>
				<Form onSubmit={this.handleSubmit}>
				 	<ReactSearchBox
				        placeholder="Type in Postcode"
				        value={this.state.input} 
				        data={this.completeSwap(this.state.location)}
				        callback={record => console.log(record)}

      				/>
					<Button color='dark' style={{ marginTop: '2rem' }} block>
                  		Search 
                	</Button>
				</Form>
			</div>


			)
	}



}
export default SearchArea
