import React from 'react';
import { Card, CardImg, CardText, CardBody, CardLink,
  CardTitle, CardSubtitle,Form, FormGroup, Label, Input,Col,Button } from 'reactstrap';
import SearchBox from "./SearchBox"
import {addItemToShop,getSuperMarkets,putPrice} from "../../actions/SuperMarketActions"
import {connect} from "react-redux"
import PropTypes from "prop-types"
import moment from "moment"
import Cleave from 'cleave.js/react';	
import { Alert } from 'reactstrap';
import grocery_sampleJSON from "../../Data/grocery_sampleJSON.json"

const source = grocery_sampleJSON

export class FormItem extends React.Component {
    constructor(props){
        super(props)
        this.state={
             result:"",
             price:"",
             visible:false,
         }
     }

     componentDidMount(){
		console.log("componentDidMount")
		this.props.getSuperMarkets()
	}
    shouldComponentUpdate(nextState,nextProps){
         if(this.state.result!==nextState.result){
             return true
         }
         if(this.props.supermarket_selected!==nextProps.supermarket_selected	){
			return true
		}
    }
    componentDidUpdate(prevProps){
    	if(prevProps.supermarket_selected!==this.props.supermarket_selected	){
    		this.setState({result:""})
    	}
    }
    visible=()=>{
		this.setState({visible:true})
	}
	onDismiss=()=> {
    	this.setState({ visible: false });
    }
	result = (result)=>{
    	this.setState({result:result})
	}
	onSubmit=(event)=>{
		event.preventDefault()
		const {result,price}=this.state
		const newItem = {
			item_id:result.item_id,
			brand_name:result.brand_name,	
			price:price,
			timeStamp:moment().format(),
			superMarket_id:this.props.supermarket_selected.id,
			date:moment().format("ddd Do MMM YY")
		}
		this.setState({
			price:"",
			visible:true})
		this.props.addItemToShop(newItem)		
	}
	onChange=(event)=>{
		this.setState({
			price:event.target.value
		})
	}
	onClick=(event)=>{
		let shoppingItems = {}
		source.forEach((item)=>{
			console.log(item)
			let eachItem = {
				item_id:item.item_id,
				brand_name:item.brand_name,	
				price:(Math.random()*(20)+0.5).toFixed(2),
				timeStamp:moment().format(),
				superMarket_id:this.props.supermarket_selected.id,
				date:moment().format("ddd Do MMM YY")
			}
			shoppingItems[item.item_id]=eachItem
			this.props.addItemToShop(eachItem)	
		})
		console.log(shoppingItems)

	}
	onStartDrag=(resultInfo,event)=>{
		console.log("drag START",resultInfo)
		this.props.itemOnDrag(resultInfo)
	}
    render() {
    	const {result,price}=this.state
    	console.log(this.state,this.props)
    	let card
		let cardImage = <CardImg top width="100%" src={this.state.result.image} alt="Card image cap"
	        style={{maxWidth:"150px",maxHeight:"150px",margin:"auto"}}/>
	        if(this.state.result.image===null){
	        	cardImage=
	        	    <CardBody className="text-center"> 
	        	    	<CardText>Be the first to upload a photo</CardText>
	        			<Input type="file" name="file" id="exampleFile"  style={{width:"60%",margin:"auto"}}/>
	        		</CardBody>
	        }	
    	const compareCard = 
    		<Card style={{marginTop:"20px"}} draggable={true} onDragStart={this.onStartDrag.bind(this,{result:this.state.result,price:price})}>
            <CardBody className="text-center">
                <CardTitle>{this.state.result.brand_name}</CardTitle>
                <CardSubtitle>{this.state.result.description}</CardSubtitle>
                
            </CardBody>
           	{cardImage}
            <CardBody>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                 <CardText>{this.state.result.price}</CardText>
    		</CardBody>
    		</Card>
		if (this.state.result===""){
				console.log("new item")
				card=null
		}else if (this.props.compareBasket===true){
			const card = compareCard
		}else {
			var oldPrice = "Be the first to register a price!"
			if(this.props.superMarket[this.props.supermarket_selected.id].item!==undefined	){
				if(this.props.superMarket[this.props.supermarket_selected.id].item[result.item_id]){
					const prevPrice = this.props.superMarket[this.props.supermarket_selected.id].item[result.item_id].price
					const prevDate = this.props.superMarket[this.props.supermarket_selected.id].item[result.item_id].date
					oldPrice = `This was last registered at $${prevPrice} on ${prevDate}`
				}
			}

		const card =
			<Card style={{marginTop:"20px"}} draggable={true} onDragStart={this.onStartDrag.bind(this,{result:this.state.result,price:price})}>
	            <CardBody className="text-center">
	                <CardTitle>{this.state.result.brand_name}</CardTitle>
	                <CardSubtitle>{this.state.result.description}</CardSubtitle>
	                
	            </CardBody>
	           	{cardImage}
	            <CardBody>
	                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
	                 <CardText>{oldPrice}</CardText>
	                <Form onSubmit={this.onSubmit}>
	                    <FormGroup row className="text-center">
	                        <Col sm={10}>
	                       		$<Cleave placeholder="Enter the Price"
	                       			options={{numeral: true}}
	                				numeralThousandsGroupStyle = 'thousand'
	                				onChange={this.onChange}
	                				value={price} />
	                        </Col>
	                    </FormGroup>
	    				<FormGroup check row>
	    					<Col sm={{ size: 10, offset: 2 }}>
	    						<Button>Submit</Button>
	     					</Col>
	    				</FormGroup>
	    			</Form>
	    		</CardBody>
	    		</Card>
		}
	return (
    	<div>
    		<h1 style={{marginTop:"20px"}}>Search an item</h1>
        	<SearchBox
            	result = {this.result}
            	supermarket_selected={this.props.supermarket_selected}/>
              <Alert color="success" isOpen={this.state.visible} toggle={this.onDismiss} style={{}}>
        		The price has been registered
      		</Alert>
        	{card}
        	{this.props.superMarket[this.props.supermarket_selected.id].item===undefined?
        		<Button onClick = {this.onClick} >Generate Item Data for shop</Button>:null}
    	</div>
   
		);
	}
}

FormItem.propTypes = {
	addItemToShop:PropTypes.func.isRequired,
	getSuperMarkets:PropTypes.func.isRequired,
	superMarket:PropTypes.object.isRequired
}

const mapStateToProps = (state)=>({
	superMarket:state.superMarket

})

export default connect(mapStateToProps,{getSuperMarkets,addItemToShop})(FormItem)
