import React, { Component } from 'react';
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker  } from "react-google-maps";
import Geocode from "react-geocode"
import Autocomplete from 'react-google-autocomplete';
import basket from "../image/basket.png"

Geocode.setApiKey('AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI');
Geocode.enableDebug();


export class Map extends Component {
	constructor(props){
		super(props)
		this.state = {
			bound:null,
			input:"",
			mapPosition:{
				lat: this.props.center.lat,
				lng: this.props.center.lng
			},
			markerPosition:{
				lat:this.props.center.lat,
				lng:this.props.center.lng
			},
			movePosition:{
				lat:this.props.center.lat,
				lng:this.props.center.lng		
			},
			map:null,
			markers:[{photo_id:0,lat: -33.78, lng: 151.2,name:"add1"},{photo_id:10,lat: -33.79, lng: 151.1,name:"add22"},
				{photo_id:2,lat: -33.785, lng: 151.23,name:"add3"},{photo_id:5,lat: -31.95, lng: 115.8605,name:"add4"},
				{photo_id:1,lat: -34.070587, lng: 150.791252,name:"add2"}],
			markers1:[],
			zoom:10,
			moving:false,
			maploading:false,

		}

	}
	componentDidMount(){
		this.updating_marker()
	}

	updating_marker = ()=>{
		console.log("updating marker>")
		Geocode.fromLatLng(this.state.mapPosition.lat,this.state.mapPosition.lng).then(
			response=>{
				const address = response.results[0];
    			console.log(address,"address");
			})
		this.props.mapCenter(this.state.mapPosition)
		if (this.state.moving==true){
			var markers1 = this.insideBound_bound(this.state.markers,this.state.bound)
			this.setState({
				markers1:markers1,
				moving:false,
				input:false,
				maploading:false
			})

		}else{
			var markers1 = this.insideBound(this.state.markers,this.state.movePosition)
			this.setState({
				markers1:markers1,
				moving:false,
				input:false,
				maploading:false,
			})
		
		}
		}
	shouldComponentUpdate( nextProps, nextState ){	
		console.log("should Componeent update", nextState, this.state)
		if (this.check_markers1(nextState.markers1,this.state.markers1)==false){
			console.log("change")
			return true
		}		
		else if(nextState.input==true){
			console.log("change position")
			return true
		}

		else if (this.check_markers1(nextState.markers1,this.state.markers1)==true){
			console.log("no change")
			return false
		}

		
	}
	componentDidUpdate(prevProps, prevState, snapshot){
		console.log("componentDidUpdate",prevState)
		this.updating_marker()
	}
	check_markers1=(array1,array2) => {
		var arr1 = []
		var arr2 = []
		array1.map((elem)=>{
			arr1.push(elem.photo_id)
		})
		array2.map((elem)=>{
			arr2.push(elem.photo_id)
		})
		if (arr1.sort().toString()==arr2.sort().toString()){	
			return true
		}else 
			{return false}
		}
	onPlaceSelected = ( place,event ) => {
		console.log(place)
		if (place.name!==undefined){
			Geocode.fromAddress(place.name).then(
				response => {
    			const { lat, lng } = response.results[0].geometry.location
    			console.log(lat, lng)
    			var markers1 = this.insideBound(this.state.markers,response.results[0].geometry.location)
    			if (lat ===undefined){
    				return null
    			}
    			this.setState({
					mapPosition: {
						lat: lat,
						lng: lng
					},
					markerPosition: {
						lat: lat,
						lng: lng
					},
					movePosition: {
						lat: lat,
						lng: lng
				},
					zoom:12,
					markers1:markers1,
					input:true,
					moving:false,

			})
    		})
		}
		else{
			const name = place.formatted_name,
			latValue = place.geometry.location.lat(),
			lngValue = place.geometry.location.lng();
			var markers1 = this.insideBound(this.state.markers,place.geometry.location)
			// Set these values in the state.
			this.setState({
				mapPosition: {
					lat: latValue,
					lng: lngValue
				},
				markerPosition: {
					lat: latValue,
					lng: lngValue
				},
				movePosition: {
					lat: latValue,
					lng: lngValue
				},
				zoom:12,
				markers1:markers1,
				input:true,
				moving:false,
			})
		}

	return

	};
	mapmoved=()=>{
		console.log("mapMoved:"+JSON.stringify(this.state.map.getBounds()))
		var markers1 = this.insideBound_bound(this.state.markers,this.state.map.getBounds())
		this.setState({
			bound:this.state.map.getBounds(),
			movePosition: {
				lat: this.state.map.getCenter().lat(),
				lng: this.state.map.getCenter().lng()
			},
			mapPosition: {
				lat: this.state.map.getCenter().lat(),
				lng: this.state.map.getCenter().lng()
			},
			markers1:markers1 ,
			moving:true	
		})
	}
	maploaded=(mapRef)=>{
		console.log("maploaded")
		if(!mapRef){
			return
		}
		this.setState({
			map:mapRef,
			maploading:true})	
	}
	onZoom = ()=>{
		console.log("zoom:"+this.state.map.getCenter(),this.state.map.getZoom())
		var markers1 = this.insideBound_bound(this.state.markers,this.state.map.getBounds())
		this.setState({
			bound:this.state.map.getBounds(),
			movePosition: {
				lat: this.state.map.getCenter().lat(),
				lng: this.state.map.getCenter().lng()
			},
			mapPosition: {
				lat: this.state.map.getCenter().lat(),
				lng: this.state.map.getCenter().lng()
			},

			markers1:markers1,
			zoom:this.state.map.getZoom(),
			moving:true	
		})
	}
	insideBound = (shops,center)=>{
		if (center!=null&&shops!=null){
			var result = []
			shops.map((shop)=>{
				if (shop.lat>center.lat-0.2 && shop.lat<center.lat+0.2 && shop.lng>center.lng-0.2 && shop.lng<center.lng+0.2){
					result.push(shop)}	
			})
			console.log(result,"lets look center",center,shops)	
			this.props.markersInBound(result)		
			return result			
		}else{return []}
		
	}
	insideBound_bound = (shops,bound)=>{
		if (bound!=null&&shops!=null){
			var result = []	

			shops.map((shop)=>{
				if (shop.lat>bound.na.j && shop.lat<bound.na.l && shop.lng>bound.ia.j && shop.lng<bound.ia.l){
					result.push(shop)}	
			})
			console.log(result,"lets look bound",bound,shops)		
			this.props.markersInBound(result)		
			return result			
		}else{return []}
		
	}
	onDblClick = (marker,event)=>{	
		var mark = this.state.markers1.filter((mark)=>{
			return mark.photo_id=== marker
		})
		mark = mark[0]
		this.props.markerSelected(mark)
	}

	
	render() {
	console.log(this.state,"rendering")
	
	const AsyncMap = withScriptjs(
		withGoogleMap(
			props => (
				<GoogleMap 
				google={this.props.google}
				ref = {this.maploaded}
				zoom={this.state.zoom}
				defaultCenter={{ lat: this.props.lat, lng: this.props.lng }}
				center={{ lat: this.state.mapPosition.lat, lng: this.state.mapPosition.lng }}
				onDragEnd={this.mapmoved}
				onZoomChanged = {this.onZoom}

				>
					<Autocomplete
						style={{
						width: '200%',
						height: '40px',
						paddingLeft: '16px',
						marginTop: '2px',
						marginBottom: '20px',
						}}
						onPlaceSelected={ this.onPlaceSelected }
						types={['(regions)']}
						placeholder={"Enter and select location"}

					/>
					<Marker 
						draggable={true}
						position={{ lat: this.state.markerPosition.lat, lng: this.state.markerPosition.lng }}
					/>
  					{this.state.markers1.map((marker) => {
  						
						return( 
    					<Marker
      						key={marker.photo_id}
      						position={{ lat: marker.lat, lng: marker.lng }}
      						icon ={basket}
      						onDblClick = {this.onDblClick.bind(this,marker.photo_id)}
    					/>
)
  						
    				})}
    				

				
				</GoogleMap>
			)
		)
	)

	let map
	map =  <div style={{width:"500px"}} >

			<AsyncMap
				googleMapURL= "https://maps.googleapis.com/maps/api/js?key=AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI&libraries=places"
				loadingElement={
				 <div style={{ height: `100%` }} />
				}
				containerElement={
				 <div style={{ height: this.props.height,
  								display: "flex",
  								flexDirection: "column-reverse"}} />
				}
				mapElement={
				 <div style={{ height: `100%` }} />
				}
				/>
			</div>
	let map1
    map1 = <div style={{height: this.props.height , backgroundColor:"blue"}} />

	return (
		<div>
			 <div className="title">
				<h1>
					Search for the Supermarkets near you
				</h1>
					
			</div>
			{map}

		</div>

	 
	)
  }
}

export default Map
