import React, { Component } from 'react';
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker  } from "react-google-maps";
import Geocode from "react-geocode"
import Autocomplete from 'react-google-autocomplete';
import basket from "../image/basket.png"

Geocode.setApiKey('AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI');
Geocode.enableDebug();


export class Map extends Component {
	state = {
		mapPosition:{
			lat: this.props.center.lat,
			lng: this.props.center.lng
		},
		markerPosition:{
			lat:this.props.center.lat,
			lng:this.props.center.lng
		},
		map:null,
		markers:[{photo_id:0,lat: -33.78, lng: 151.2,name:"add1"},{photo_id:10,lat: -33.79, lng: 151.1,name:"add22"},{photo_id:2,lat: -33.785, lng: 151.23,name:"add3"}],
		bound:null,
		markers1:[],
	}

	shouldComponentUpdate( nextProps, nextState ){
		if (
		nextState.mapPosition.lat !== this.state.mapPosition.lat 
		) {
		return true
		} else if ( this.state.mapPosition.lat  === nextState.mapPosition.lat ){
		return false
		}
	 }

	onPlaceSelected = ( place,event ) => {
		console.log(place)
		if (place.name!==undefined){
			Geocode.fromAddress(place.name).then(
				response => {
    			const { lat, lng } = response.results[0].geometry.location
    			console.log(lat, lng)
    			if (lat ===undefined){
    				return null
    			}
    			this.setState({
					mapPosition: {
						lat: lat,
						lng: lng
					},
					markerPosition: {
						lat: lat,
						lng: lng
				},
			})
    		})
			return
		}
		else{
			const name = place.formatted_name,
			latValue = place.geometry.location.lat(),
			lngValue = place.geometry.location.lng();
			// Set these values in the state.
			this.setState({

				mapPosition: {
					lat: latValue,
					lng: lngValue
				},
				markerPosition: {
					lat: latValue,
					lng: lngValue
				},
			})
	}

	};
	mapmoved=()=>{
		console.log(this.state)
		console.log("drag:"+this.state.map.getCenter().lat())
		console.log("bound:"+JSON.stringify(this.state.map.getBounds()))
		this.setState({
			bound:this.state.map.getBounds(),
			mapPosition: {
					lat: this.state.map.getCenter().lat(),
					lng: this.state.map.getCenter().lng()
				},
		})
	}
	maploaded=(map)=>{
		if (this.state.map !=null){
			return
		}
		console.log(map,"asfsadfasdsadfs")
		this.setState({
			map:map,
			bound:map.getBounds()
		})
		
	}
	onZoom = ()=>{
		console.log("zoom:"+JSON.stringify(this.state.map.getCenter()))
		this.setState({
			bound:this.state.map.getBounds()
		})
		console.log(this.state	)
	}
	insideBound = (lat,lng,bound)=>{
		if (bound!=null){
			if (lat>bound.na.j && lat<bound.na.l && lng>bound.ia.j && lng<bound.ia.l){
				return true
			}else{return false}
		}
		return false		
	}

	render() {
		console.log(this.state)
	const AsyncMap = withScriptjs(
		withGoogleMap(
			props => (
				<GoogleMap 
				google={this.props.google}
				ref = {this.maploaded}
				defaultZoom={10}
				defaultCenter={{ lat: this.state.mapPosition.lat, lng: this.state.mapPosition.lng }}
				onDragEnd={this.mapmoved}
				onZoomChanged = {this.onZoom}
				>
					<Marker 
						name={'Dolores park'}
						draggable={true}
						position={{ lat: this.state.markerPosition.lat, lng: this.state.markerPosition.lng }}
					/>
  					{this.state.markers.map((marker) => {
  						if(this.insideBound(marker.lat,marker.lng,this.state.bound)==true){
  							return( 
		    					<Marker
		      						key={marker.photo_id}
		      						position={{ lat: marker.lat, lng: marker.lng }}
		      						icon ={basket}
		      						draggable={true}
		    					/>)
  						}else{return null}
    				})}
    				{this.state.markers.map((marker) => {
  						if(this.insideBound(marker.lat,marker.lng,this.state.bound)==true){
  							return( 
		    					<InfoWindow
		    						key={marker.photo_id}
		   							onClose={this.onInfoWindowClose}
		   							position={{ lat: ( marker.lat + 0.02 ), lng: marker.lng }}
		   							draggable={true}
		  						>
		   							<div>
		    							<span style={{ padding: 0, margin: 0 }}>{ marker.name }</span>
		   							</div>
		  						</InfoWindow>)
  						}else{return null}
 					 })}

					<Autocomplete
						style={{
						width: '200%',
						height: '40px',
						paddingLeft: '16px',
						marginTop: '2px',
						marginBottom: '100px'
						}}
						onPlaceSelected={ this.onPlaceSelected }
						types={['(regions)']}
						placeholder={"Enter and select location"}

					/>
				
				</GoogleMap>
			)
		)
	)

	

	return (
		<div>
			 <div>
				<h1>
					Search for the Supermarkets near you
				</h1>
					
			</div>
			 <div style={{width:"500px"}}>

				<AsyncMap
					googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBiffp6dvrIk_EWQHzXk05VzFKb5tioZeI&libraries=places"
					loadingElement={
					 <div style={{ height: `100%` }} />
					}
					containerElement={
					 <div style={{ height: this.props.height }} />
					}
					mapElement={
					 <div style={{ height: `100%` }} />
					}
				/>
			</div>
		</div>

	 
	)
  }
}

export default Map
