import React, {Component} from "react"
import CompareBasket from "./CompareBasket"
import FormItem from "./AddItem/FormItem"
import { Container, Row,Col } from 'reactstrap';
export class AddItem extends Component{
	constructor(props){
		super(props)
		this.state={
			supermarket_selected:"",
			visible:false,
		}
	}
	visible=()=>{
		this.setState({visible:true})
	}
	onDismiss=()=> {
    	this.setState({ visible: false });
    }
	supermarket_selected=(markerObject)=>{
		this.setState({supermarket_selected:markerObject})
	}
	render(){
		let shopSelected = 
			<Container style={{marginTop:"50px"}}>
				<Row>
					<Col sm={4}>
						<FormItem
					supermarket_selected={this.state.supermarket_selected} visible={this.visible}
						/>
					</Col>
					<Col sm={7}>
						<h1>Characteristic of {this.state.supermarket_selected.name}</h1>
					</Col>
				</Row>
			</Container>
		if(this.state.supermarket_selected===""){shopSelected=null}
		return (
			<React.Fragment>
				<Alert color="success" isOpen={this.state.visible} toggle={this.onDismiss}>
        			I am an alert and I can be dismissed!
      			</Alert>
				<CompareBasket
					supermarket_selected={this.supermarket_selected}
					fromAddItem = {true}
					/>
				{shopSelected}
			</React.Fragment>
			)
	}
}

export default AddItem
