import uuid from "uuid"
import {GET_SUPERMARKETS,ADD_SUPERMARKET,DELETE_SUPERMARKET} from '../actions/types';

const initialState ={
	markers:[
		{id: uuid(),lat: -33.8959144,lng: 151.0984299,name: "Shop 1",address: "26 Stanley Street",score: "",details: "",city: "Croydon Park",state: "NSW",
		postcode: "2133",completeAddress: "26 Stanley Street Croydon Park NSW 2133",items:{}},
		{id: uuid(),lat: -33.7750542,lng: 151.0397441,name: "Shop 2",address: "58 Farnell Avenue",score: "",details: "",city: "Carlingford",state: "NSW",
		postcode: "2118",completeAddress: "58 Farnell Avenue Carlingford NSW 2118",items:{}},
		{id: uuid(),lat: -34.08779579999999,lng: 150.7909654,name: "Shop 3",address: "3 Gargery Street",score: "",details: "",city: "Ambarvale",state: "NSW",
		postcode: "2560",completeAddress: "3 Gargery Street Ambarvale NSW 2560",items:{}},
	]

}

export default function (state=initialState, action) {
	switch(action.type){
		case GET_SUPERMARKETS:
			return {
				...state
			}
		case ADD_SUPERMARKET:

			return {
				...state,
				markers:[action.payload,...state.markers],
				[action.payload.id]:action.payload
			}
		default:
		 return state

	}
}
