import React from 'react';
import { Card, CardImg, CardText, CardBody, CardLink,
  CardTitle, CardSubtitle,Form, FormGroup, Label, Input,Col,Button } from 'reactstrap';
import SearchBox from "./SearchBox"
import {addItemToShop,getSuperMarkets,putPrice} from "../../actions/SuperMarketActions"
import {connect} from "react-redux"
import PropTypes from "prop-types"


export class FormItem extends React.Component {
    constructor(props){
        super(props)
        this.state={
             result:"",
             price:""
         }
     }
     componentDidMount(){
		console.log("componentDidMount")
		this.props.getSuperMarkets()
	}
    shouldComponentUpdate(nextState,nextProps){
         if(this.state.result!==nextState.result){
             return true
         }
         if(this.props.supermarket_selected!==nextProps.supermarket_selected	){
			return true
		}
    }
    componentDidUpdate(prevProps){
    	if(prevProps.supermarket_selected!==this.props.supermarket_selected	){
    		this.setState({result:""})
    	}
    }
	result = (result)=>{
    	this.setState({result:result})
	}
	onSubmit=(event)=>{
		event.preventDefault()
		const {result,price}=this.state
		const newItem = {
			item_id:result.item_id,
			brand_name:result.brand_name,	
			price:price,
			timeStamp:new Date(),
			superMarket_id:this.props.supermarket_selected.id
		}

		this.props.addItemToShop(newItem)
				
		
	}
	onChange=(event)=>{
		this.setState({
			[event.target.name]:event.target.value
		})
	}
    render() {
    	const {result,price}=this.state
    	const oldPrice = <CardText>Be the first to register a price!</CardText>
		console.log(this.props.superMarket[this.props.supermarket_selected])
		if(this.props.superMarket[this.props.supermarket_selected].item!==undefined	){
			if(this.props.superMarket[this.props.supermarket_selected].item[result.item_id]){
				const prevPrice = this.props.superMarket[this.props.supermarket_selected].item[result.item_id].price
				const prevDate = this.props.superMarket[this.props.supermarket_selected].item[result.item_id].timeStamp
				oldPrice = <CardText>This was last registered at {prevPrice} on {prevDate}</CardText>
    		}
		}
    	
        let card =
            <Card style={{marginTop:"20px"}}>
            <CardBody className="text-center">
                <CardTitle>{this.state.result.brand_name}</CardTitle>
                <CardSubtitle>{this.state.result.description}</CardSubtitle>
                
            </CardBody>
            	<CardImg top width="100%" src={this.state.result.image} alt="Card image cap"
             		style={{maxWidth:"150px",maxHeight:"150px",margin:"auto"}}/>

            <CardBody>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                {oldPrice}
                <Form onSubmit={this.onSubmit}>
                    <FormGroup row className="text-center">
                    </FormGroup>
                    <FormGroup row className="text-center">
                        <Label for="price">Price</Label>
                        <Col sm={8}>
                       		<Input name="price" placeholder="Price" onChange={this.onChange	}/>
                        </Col>
                    </FormGroup>
    				<FormGroup check row>
    					<Col sm={{ size: 10, offset: 2 }}>
    						<Button>Submit</Button>
     					</Col>
    				</FormGroup>
    			</Form>
    		</CardBody>
    		</Card>
    			if (this.state.result===""){
       				console.log("new item")
       				card=null
    			}
	return (
    	<div>
        	<SearchBox
            	result = {this.result}
            	supermarket_selected={this.props.supermarket_selected}/>
        	{card}
    	</div>
   
		);
	}
}

FormItem.propTypes = {
	addItemToShop:PropTypes.func.isRequired,
	getSuperMarkets:PropTypes.func.isRequired,
	superMarket:PropTypes.object.isRequired
}

const mapStateToProps = (state)=>({
	superMarket:state.superMarket

})

export default connect(mapStateToProps,{getSuperMarkets,addItemToShop})(FormItem)
