import React, {Component} from "react"
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input
} from 'reactstrap';
import ReactSearchBox from 'react-search-box'


class SearchArea extends Component{
	state = {
		input:"",
		location:["Penrith, 2750","Sydney, 2000","Blacktown, 2148"]
	}
	onSelect = (event)=>{
		console.log(event)
		var input = ""
		typeof event.key === 'string'?input=event.key:input = event.value
		this.setState({
			input:input
		})
	}
	completeSwap=(jsonPoint)=>{
		var newArray = []
		jsonPoint.map((point)=>{
			var newJson = {}
			newJson["value"]=point["key"]
			newJson["key"]=point["value"]
			newArray.push(newJson)

		})
		return [...newArray,...jsonPoint]
	}
	handleSubmit =(event)=>{
		event.preventDefault()
		console.log(event.target.value)
		this.props.area(event.target.value)
		this.setState({input:""})
	}
	
	render(){
		console.log(this.state)
		return (
			<div>
				<h1>
					Search for the Supermarkets near you
				</h1>
				<Form onSubmit={this.handleSubmit}>
				 	<ReactSearchBox
				        placeholder="Type in Postcode"
				        value={this.state.input} 
				        data={this.completeSwap(this.state.location)}
				        callback={record => console.log(record)}
				        onSelect ={this.onSelect}
      				/>
					<Button color='dark' style={{ marginTop: '2rem' }} block>
                  		Search 
                	</Button>
				</Form>
			</div>


			)
	}



}
export default SearchArea
